    "body": "2022/07/19 - 12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061package mainimport  fmt type Node struct {	Val int	Next *Node //invalid recursive type}type Queue struct {	Head *Node	Tail *Node	// if we dont have Tail,	// everytime we want to enqueue, we have to traverse from beginning	Length int // more convenient}func (q *Queue) Enqueue(data int) {	newnode := &amp;Node{data, nil}	if q. Length == 0 { //1st, nothing		q. Head = newnode	} else if q. Tail != nil { //2nd, 3rd. . . 		q. Tail. Next = newnode	}	q. Tail = newnode	q. Length++	// q. Tail. Next. Val = data	// Since q. Tail is nil, nil. Next leads to  nil pointer dereference }func (q *Queue) Dequeue() int {	if q. Length == 0 {		return -1	}	dropnode := q. Head	if q. Length == 1 { //last time		q. Head, q. Tail = nil, nil	} else if q. Head != nil {		q. Head = q. Head. Next	}	q. Length--	return dropnode. Val}func main() {	q := &amp;Queue{}	q. Enqueue(0)	q. Enqueue(1)	q. Enqueue(2)	drop1 := q. Dequeue()	drop2 := q. Dequeue()	drop3 := q. Dequeue()	fmt. Println(q, drop1, drop2, drop3)}"
